
public class WrapperClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =30;
		byte by = 2;
		Integer obj = new Integer(a);
		Byte b = new Byte(by);
		
		System.out.println(obj);
		
		int c = obj;
		System.out.println(c);
		
		byte d = b;
		System.out.println(d)
		;

	}

	

}
